def python_function(xs: list):
    if len(xs) % 2 != 0:
        raise ValueError("xs must have an even number of coefficients")
    if abs(xs[0]) < abs(xs[-1]):
        raise ValueError("xs must have the largest non-zero coefficient as the first element")

